import React from "react";
import styled from "styled-components";
import { Device, Strings } from "../../Constant";
import InputArea from "../../Common/InputArea";
import AppLogo from "../../Assets/AppLogo.png";
import { Link } from "react-router-dom";
import { loadData, saveData } from "../../utils/LocalStorage";
const WorkspaceDetails = (props) => {
  let valueName = loadData("name");
  let valueDisplayName = loadData("Display-name");
  let info = {
    name: valueName ?? "",
    displayName: valueDisplayName ?? "",
  };

  const [query, setQuery] = useState(info);

  const onHandleChange = (e) => {
    const { name, value } = e.target;
    setQuery({
      ...query,
      [name]: value,
    });
  };
  return (
    <div
      style={{
        textAlign: "left",
        // border: "1px solid red",
        display: "flex",
        width: "34rem",
        flexDirection: "column",
        marginTop: "30px",
      }}
    >
      <div
        style={{
          display: "flex",
          // border: "1px solid red",
          justifyContent: "center",
          marginRight: "21%",
        }}
      >
        <img
          src={AppLogo}
          style={{
            height: "30px",
            width: "30px",
            marginTop: "17px",
            marginRight: "3%",
          }}
        />
        <h2>Eden</h2>
      </div>
      <Header textHead={Strings.WELCOME} subText={Strings.YOU_CAN_CHANGE} />
      <h5 style={{ color: "gray", padding: 5 }}>Full Name</h5>
      <InputArea
        placeholder="Steve jobs"
        header={"Full Name"}
        setQuery={setQuery}
        text={"name"}
        value={query.name}
        onHandleChange={onHandleChange}
      />
      <h5 style={{ color: "gray", padding: 5 }}>Display Name</h5>
      <InputArea
        placeholder="Steve"
        header={"Display Name"}
        setQuery={setQuery}
        text={"displayName"}
        value={query.displayName}
        onHandleChange={onHandleChange}
      />

      {query.name && query.displayName ? (
        <Link
          style={{ outline: "none", textDecoration: "none" }}
          to="/workspace"
        >
          <div
            style={{
              background: "#6A5EE5",
              paddingLeft: "19px",
              paddingTop: "12px",
              paddingBottom: "12px",
              width: "391px",
              borderRadius: "5px",
              color: "white",
              textAlign: "center",
              outline: "none",
              textDecoration: "none",
            }}
            onClick={() => handleSubmit()}
          >
            Create Workspace
          </div>
        </Link>
      ) : (
        <>
          {" "}
          <div
            style={{
              background: "#6A5EE5",
              paddingLeft: "19px",
              paddingTop: "12px",
              paddingBottom: "12px",
              width: "391px",
              borderRadius: "5px",
              color: "white",
              textAlign: "center",
              outline: "none",
              textDecoration: "none",
            }}
            onClick={() => handleSubmit()}
          >
            Create Workspace
          </div>
        </>
      )}
    </div>
  );
};

export default WorkspaceDetails;
