import React, { useState } from "react";
import Header from "./Header";
import styled from "styled-components";
import { Device, Strings } from "../../Constant";
import InputArea from "../../Common/InputArea";
import AppLogo from "../../Assets/AppLogo.jpg";
const Main = styled.div`
  width: 60%;
  height: 400;
  margin-top: 5%;
  margin-left: 20%;
  background-color: white;

  @media ${Device.mobileS} {
    background-color: white;
  }
  @media ${Device.mobileM} {
    background-color: blue;
  }
  @media ${Device.mobileL} {
    background-color: pink;
  }
  @media ${Device.laptop} {
    background-color: purple;
  }
  @media ${Device.laptopL} {
    background-color: white;
  }
  @media ${Device.desktop} {
    background-color: red;
  }
`;

const WelcomeScreen = (props) => {
  let info = {
    name: "",
    displayName: "",
  };

  const [query, setQuery] = useState(info);

  const onHandleChange = (e) => {
    const { name, value } = e.target;
    setQuery({
      ...query,
      [name]: value,
    });
  };

  return (
    <div
      style={{
        textAlign: "left",
        border: "1px solid red",
        display: "flex",
        width: "34rem",
        flexDirection: "column",
      }}
    >
      <div>
        <img source={app} />
      </div>
      <h1>{Strings.WELCOME}</h1>
      <span style={{ marginLeft: "16%" }}>{Strings.YOU_CAN_CHANGE}</span>
      <h5 style={{ color: "gray", padding: 5 }}>Full Name</h5>
      <InputArea
        placeholder="Steve jobs"
        header={"Full Name"}
        setQuery={setQuery}
        text={"name"}
        value={query.name}
        onHandleChange={onHandleChange}
      />
      <h5 style={{ color: "gray", padding: 5 }}>Display Name</h5>
      <InputArea
        placeholder="Steve jobs"
        header={"Full Name"}
        setQuery={setQuery}
        text={"name"}
        value={query.name}
        onHandleChange={onHandleChange}
      />
      <div
        style={{
          background: "#6A5EE5",
          paddingLeft: "19px",
          paddingTop: "12px",
          paddingBottom: "12px",
          width: "391px",
          borderRadius: "5px",
          color: "white",
          textAlign: "center",
        }}
        onClick={() => alert("1")}
      >
        Create Workspace
      </div>
    </div>
  );
};

export default WelcomeScreen;
